import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { DatePipe } from '@angular/common';

@Component({
  template: `<router-outlet></router-outlet>`,
})
export class OrdersComponent {
}

@Component({
  templateUrl: './order-list.component.html',
  styleUrls: ['./orders.component.css']
})
export class OrderListComponent {
  orders: Array<{ oNumber: number, cNumber: number, cAdress: string, totalCost: number, status: string }> =
    [
      { oNumber: 1, cNumber: 1, cAdress: "Test", totalCost: 5, status: "New"},
    ];
}


@Component({
  templateUrl: './new-order.component.html',
  styleUrls: ['./orders.component.css'],
  providers: [DatePipe]
})
export class NewOrderComponent {
  orderForm: any;
  currentDate = new Date();
  products: Array<{ id: number, name: string, category: string, size: number, quantity: number, price: number }> =
    [
      { id: 1, name: "First", category: "Test", size: 5, quantity: 2, price: 50 },
      { id: 2, name: "Second", category: "Test", size: 15, quantity: 1, price: 100 },
    ];
  total: string;
  currFormater = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
  });

  constructor(private formBuilder: FormBuilder) {
    this.CreateForm();
    this.total = this.currFormater.format(this.getTotal());
  }
  CreateForm() {
    this.orderForm = this.formBuilder.group({
      'orderNumber': [''],
      'orderDate': [this.currentDate.toLocaleDateString()],
      'customer': ['', [Validators.required]],
      'status': ['', [Validators.required]],
      'totalCost': [''],
      'products': [this.products],
      'comment': ['']
    });
  }

  addProduct(): void {
    this.products.push(
      { id: 1, name: "First", category: "Test", size: 5, quantity: 2, price: 50 }
    );
    this.total = this.currFormater.format(this.getTotal());
  }

  saveOrder(): void {
    if (this.orderForm.dirty && this.orderForm.valid) {
      console.log(this.orderForm.value);
    }
  }

  getTotal(): number {
    let total = 0;
    for (const element of this.products) {
      total += element.quantity * element.price;
    }
    return total;
  }
}

<nav class="navbar navbar-expand-sm navbar-toggleable-sm navbar-light bg-white border-bottom box-shadow">
  <div class="container">
    <h1 class="navbar-brand">Orders</h1>
    <div class="navbar-collapse d-sm-inline-flex justify-content-end">
      <ul class="navbar-nav flex-grow">
        <li class="nav-item" [routerLinkActive]="['link-active']">
          <a class="nav-link text-dark" [routerLink]="['../new']">New Order</a>
        </li>
      </ul>
    </div>
  </div>
</nav>
<table>
  <thead>
    <tr>
      <th>Order Number</th>
      <th>Customer Number</th>
      <th>Customer Adress</th>
      <th>Total Cost</th>
      <th>Status</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let order of orders">
      <td>{{order.oNumber}}</td>
      <td>{{order.cNumber}}</td>
      <td>{{order.cAdress}}</td>
      <td>{{order.totalCost}}</td>
      <td>{{order.status}}</td>
    </tr>
  </tbody>
</table>

<nav class="navbar bg-white border-bottom box-shadow">
  <div class="container">
    <h1 class="navbar-brand">New Order</h1>
  </div>
</nav>

<div class="container">
  <form class="form-horizontal" [formGroup]="orderForm" (submit)="saveOrder()">
    <div>
      <label class="control-label col-sm-2" for="orderNumber">Order Number:</label>
      <input class="form-control" formControlName="orderNumber" id="orderNumber" readonly />

      <label class="control-label col-sm-2" for="orderDate">Order Date:</label>
      <input class="form-control" formControlName="orderDate" id="orderDate" readonly />

      <label class="control-label col-sm-2" for="customer">Customer <sup>(required)</sup> :</label>
      <select class="form-select" formControlName="customer" id="customer">
        <option disabled selected value="">Select Customer</option>
        <option>John</option>
        <option>Josh</option>
        <option>Jotaro</option>
        <option>Justin</option>
      </select>

      <label class="control-label col-sm-2" for="status">Status <sup>(required)</sup> :</label>
      <select class="form-select" formControlName="status" id="status">
        <option disabled selected value="">Select Status</option>
        <option>New</option>
        <option>Paid</option>
        <option>Shipped</option>
        <option>Delivered</option>
        <option>Closed</option>
      </select>

      <label class="control-label col-sm-2" for="totalCost">Total Cost:</label>
      <input class="form-control" [(ngModel)]="total" formControlName="totalCost" id="totalCost" readonly />

      <label class="control-label col-sm-2" for="products">Add Products:</label>
      <button type="button" (click)="addProduct()"> Add</button>
      <table formArrayName="products">
        <thead>
          <tr>
            <th>Product Id</th>
            <th>Product Name</th>
            <th>Product Category</th>
            <th>Product Size</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let product of products">
            <td>{{product.id}}</td>
            <td>{{product.name}}</td>
            <td>{{product.category}}</td>
            <td>{{product.size}}</td>
            <td>{{product.quantity}}</td>
            <td>{{product.price}}</td>
          </tr>
        </tbody>
      </table>

      <label class="control-label col-sm-2" for="comment">Comment:</label>
      <textarea class="form-control" formControlName="comment" id="comment"></textarea>
    </div>
    <br />
    <nav class="navbar">
      <div class="container navbar-collapse d-sm-inline-flex justify-content-end">
        <button type="submit" class="btn" [disabled]="!orderForm.valid">Submit</button>
        <button type="button" class="btn" [routerLink]="['../list']">Cancel</button>
      </div>
    </nav>
  </form>
</div>
